mapper.select:
SELECT t.id as order_id,t.user_id as user_id,t.total_money,t.order_date,t.order_state, tl.user_name,tl.user_province,tl.user_city,tl.user_area,tl.user_location,tl.user_phone FROM t_b_order t,t_b_location tl WHERE 1=1 AND t.location_id=tl.id
AND t.order_date >=to_date(#{order_start_date},'yyyy-mm-dd')
AND t.order_date <=to_date(#{order_over_date},'yyyy-mm-dd')
AND t.id=#{id}
AND t.user_id=#{user_id}

SELECT goods_id,goods_num
FROM t_b_order_detail
WHERE order_id=#{order_id}
